import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { getProductData } from "./Api";
//import { HiArrowNarrowLeft } from "react-icons/hi";


function ProductDetails() {
  const id = +useParams().id;
  const [product, setProduct] = useState();

  useEffect(
    function (id) {
      const promise = getProductData(id);
      promise.then(function (product) {
        setProduct(product);
        console.log("response.data is", product);
      });
    }, [id]);

  if (!product) {
    return (<div className="text-lg text-blue-800">....loading</div>);
  }

  return (
    <>

      <div className="w-20 h-20">
        <img src={product.thumbnail} />
        <div>{product.title}</div>
        <div>price is {product.price}</div>
        <input type={Number} />
        <button className="rounded-md text-yellow-600"> Add to cart </button>
        <discription />
      </div>
      <Link className="text-red-500 bg-yellow-200 mt-2 rounded w-15" to="/"> back to home </Link>
    </>
  );
}

export default ProductDetails;




