import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { getProductData } from "./Api";
import Loading from "./Loading";
import { HiArrowNarrowLeft, HiArrowNarrowRight } from "react-icons/hi";
import NotFound from "./NotFound";


function ProductDetails({ onAddToCart }) {
  const id = +(useParams().id);
  const [prod, setProduct] = useState();
  const [loading, setLoading] = useState(true);
  const [count, setCount] = useState(1);

  function handleCount(event) {
    setCount(+event.target.value);
  }

  useEffect(function () {
    const p = getProductData(id);
    p.then(function (response) {
      setProduct(response);
      setLoading(false);
      console.log("response.data is", response.data);
    }).catch(function () {
      console.log("API ERRor");
      setLoading(false);

    })
      ;
  }, [id]);

  function HandleclickButton() {
    onAddToCart(id, count);
  }

  if (loading) {
    return (<Loading />);
  }
  if (!prod) {
    return (<div className="flex items-center justify-center"><NotFound /></div>);
  }

  return (
    <div>

      <Link className="text-indigo-900 mt-2 bg-grey-700 flex items-center justify-center text-3xl " to="/"><HiArrowNarrowLeft />Back to Home page </Link>

      <div className="bg-red-300 p-2 rounded-md max-w-xs w-20 h-20">
        <img className="" src={prod.thumbnail} /></div>
      <div className="px-2">{prod.title}</div>
      <div className="px-2">price is {prod.price} Rs.</div>
      <p className="px-2">{prod.discription}</p>
      <div className="px-4 py-2 flex  ml-1 ">
        <input type="Number" onChange={handleCount} value={count} className=" rounded-md text-yellow-600" />
        <button onClick={HandleclickButton} className="bg-red-300 rounded-md"> Add to cart </button>
      </div>
      <discription />
 
      <div className="flex justify-between">
        {id > 1 && (<Link className=" mt-2 flex items-center text-3xl" to={"/Products/" + (id - 1)}><HiArrowNarrowLeft /> Previous</Link>)}
        <Link className="flex items-center text-3xl" to={"/Products/" + (id + 1)}><HiArrowNarrowRight /> Next</Link>
      </div>

    </div>
  )
}
export default ProductDetails;




