 }

  return (
    <div className="">
      <span>
    <Link className="text-indigo-900 mt-2 flex items-center text-4xl" to="/"><HiArrowNarrowLeft/> </Link>
        
      <div className="bg-red-300 p-2 rounded-md max-w-xs w-20 h-20">
        <img src={prod.thumbnail} />
        <div>{prod.title}</div>
