import React, { useState } from 'react';
import Product from './Product';
import ProductList from './ProductList';

function App() {
const [query,setQuery] = useState ("");
const [sort, setsort] =useState ("price");

  let alldata=[
    {imgurl:"https://www.shutterstock.com/image-photo/black-ceramic-mug-on-white-600w-1614896335.jpg",
    price:342,
    title:"coffee mug",
    category:"mugs"},

    {imgurl:"https://www.shutterstock.com/image-vector/realistic-white-cup-isolated-on-600w-585104080.jpg",
    price:"3424",
    title:"white mug",
    category:"mugs"},

    {imgurl:"https://www.shutterstock.com/image-photo/close-man-blank-tshirt-600w-140861086.jpg",
    price:"3467",
    title:"Black t-shirt",
    category:"tshirt"}
  ];

  let data= alldata.filter(function(item){
    return item.title.toLowerCase().indexOf(query.toLowerCase()) !=-1;
  });

//const [data,setData]=useState(alldata);
 // let data=alldata;
  function handleSearchchange(event){
    setQuery(event.target.value);}

//setQuery(newQuery);
//setData(newData);
if(sort=="price"){
data.sort(function (item1,item2){
  return item1.price-item2.price;
});
}
else if(sort=="name"){
  data.sort(function(x,y){
  return (x.title < y.title ? -1 : 1);
  });
}

  function handleSortchange(event){
    setsort(event.target.value);
  }

  return (
    <div className='flex gap-3'>
     Hello world
     <input 
     value={query}
     placeholder="search"
     className="border-gray-500 rounded-md bg-pink-300 text-cyan-800"
     onChange={handleSearchchange}
     />
     <select 
     value={sort}
     onChange={handleSortchange} >
     <option value="default">Default sort</option>
     <option value ="name">sort by name</option>
      <option value="price">sort by price</option>
     </select>
     <ProductList products= {data}/>


      </div> 
  );
}

export default App;
