function App() {
   // const path = window.location.pathname;
    return (
       <div className='bg-gray-200 h-screen overflow-y-scroll flex flex-col'>
         <Navbar/>  
         <div>
         <Routes>
<Route index element={<ProductListPage />}></Route>
<Route path="/products/:id" element={<ProductDetails />}></Route>
</Routes>
</div>
<Navbar/> 

        </div>

        );
}

export default App;




