    return (<Loading />);
  }
  if (!prod) {
    return (<div className="flex items-center justify-center"><NotFound /></div>);
  }

  return (
    <div>

      <Link className="text-indigo-900 mt-2 flex items-center text-4xl" to="/"><HiArrowNarrowLeft />Back to Home </Link>

      <div className="flex justify-between">
        {id > 1 && (<Link className=" mt-2 flex items-center text-3xl" to={"/Products/" + (id - 1)}><HiArrowNarrowLeft /> Previous</Link>)}
        <Link className="  flex items-center text-3xl" to={"/Products/" + (id + 1)}><HiArrowNarrowRight /> Next</Link>
      </div>

      <div className="bg-red-300 p-2 rounded-md max-w-xs w-20 h-20">
        <img src={prod.thumbnail} />
        <div>{prod.title}</div>
        <div>price is {prod.price}</div>
        <p>{prod.discription}</p>
        <div className="flex px-2 py-1 ml-2 ">
